import AxiosClient from "./AxiosClient"
import getToken from "./TokenFetcher"

export interface apiImage {
    id: string
    src: string
}


export const getImages = async (page: number): Promise<apiImage[]> => {
    const token = await getToken()
    try {
        const response = await AxiosClient.get('/images', {
            params: { page }, headers: {
                authorization: `Bearer ${token}`
            }
        })
        response.data
    } catch (error) {
        throw Error(error)
    }
}

export const getImageDetails = async (id: string) => {
    const token = await getToken()
    try {
        const response = await AxiosClient.get('/images', {
            params: { id }, headers: {
                authorization: `Bearer ${token}`
            }
        })
        return response
    } catch (error) {
        throw Error(error)
    }
}
