import AxiosClient from "./AxiosClient"
import getToken from "./TokenFetcher"

export interface apiImage {
    id: string
    src: string
}


export const getImages = (page: number): apiImage[] => {
    const token = getToken()
    let images: apiImage[] = AxiosClient.get('/images', {
        params: { page }, headers: {
            authorization: `Bearer ${token}`
        }
    }).then(
        (imageList) => images = imageList
    )

    return images
}

export const getImageDetails = async (id: string) => {
    const token = await getToken()
    try {
        const response = await AxiosClient.get('/images', {
            params: { id }, headers: {
                authorization: `Bearer ${token}`
            }
        })
        return response
    } catch (error) {
        throw Error(error)
    }
}
