import React, { ReactElement, FC, useState, } from "react";
import { picture } from "../../utils/ImageFetcher";
import ImageDetail from "../../pages/ImageDetail";

interface imageDisplayerProps {
    pictures: picture[]
}


const ImageDisplayer: FC<imageDisplayerProps> = ({ pictures }): ReactElement => {
    const [detailImage, setdetailImage] = useState<string>('')

    return (
        <section className='flex flex-wrap justify-center'>
            {
                pictures.map((picture) =>
                    <img className='object-contain w-1/2 md:w-1/5 lg:w-1/12 lg:mx-2 border-green-600 border' src={picture.cropped_picture} />
                )
            }
            <ImageDetail imageId={detailImage} close={setdetailImage} />
        </section>
    )
}

export default ImageDisplayer
