import React, { ReactElement, FC, useEffect, useState } from "react";
import { fetchImageDetails, imageDetail } from "../../utils/ImageFetcher";

interface detailProps {
    imageId: string
    close: (id: string) => void
}

const ImageDetail: FC<detailProps> = ({ imageId, close }): ReactElement => {
    const [details, setDetails] = useState<imageDetail | undefined>()

    useEffect(() => {
        imageId && fetchImageDetails(imageId).then(
            res => setDetails(res)
        )
    }, [imageId])



    return <div className={`${imageId !== '' ? 'absolute' : 'hidden'} rounded-lg bg-white border w-1/2`}>
        <h2>

        </h2>
    </div>
}

export default ImageDetail
