import React, { ReactElement, FC, useState, useEffect } from "react";
import { apiImage, getImages } from "../../utils/ImageFetcher";


const Main: FC = (): ReactElement => {
    const [page, setPage] = useState<number>(0)
    const [currentImages, setCurrentImages] = useState<apiImages>()
    const loadedPages: apiImage[][] =


        useEffect(() => {
            const currentPageImages = async () => await getImages(page)
            setCurrentImages(currentPageImages())
        }, [])


    return (
        <main>
            <h1>
                AgileEngine Interview
            </h1>

            <div className='flex flex-row justify-between'>
                <button className='bg-blue-400 rounded' onClick={() => page > 0 && setPage(page - 1)}>
                    Previous
                </button>
                <p>
                    Current page: {page}
                </p>
                <button className='bg-blue-400 rounded' onClick={() => setPage(page + 1)}>
                    Next
                </button>
            </div>
        </main>
    )
}


export default Main
