import React, { ReactElement, FC, useEffect, useState } from "react";
import { fetchImageDetails, imageDetail } from "../../utils/ImageFetcher";

interface detailProps {
    imageId: string
    setImageId: (id: string) => void
}

const ImageDetail: FC<detailProps> = ({ imageId, setImageId }): ReactElement => {
    const [details, setDetails] = useState<imageDetail | undefined>()

    useEffect(() => {
        imageId && fetchImageDetails(imageId).then(
            res => setDetails(res)
        )
    }, [imageId])

    const close = () => setImageId('')

    const getIndividualTags = (fullTags: string): string[] => {
        return fullTags.split(' ')
    }

    return (
        <section className={`${imageId !== '' ? 'absolute' : 'hidden'} w-full px-2`}>
            <div className={`rounded-lg bg-white border-2 w-full p-4 box-shadow`}>
                <h2 className='mb-2'>
                    Picture ID:
                    <strong>
                        {imageId}
                    </strong>
                </h2>
                <img src={details?.full_picture} alt={imageId} className='mb-2 object-contain' />
                <p className='mb-2'>
                    Author: {details?.author}
                </p>
                <p className='mb-2'>Camera with which the picture was taken: {details?.camera}</p>

                <button className='rounded p-2 bg-blue-400' onClick={close}>
                    Close
                </button>
            </div>
        </section>
    )
}

export default ImageDetail
