import AxiosClient from "./AxiosClient"
import getToken from "./TokenFetcher"

interface imagePage {
    hasMore: true
    page: 1
    pageCount: 26
    pictures: {
        id: string
        cropped_picture: string
    }[]
}

export const getImages = async (page: number): Promise<imagePage[]> => {
    const { token } = await getToken()
    console.log(token)
    try {
        const response = await AxiosClient.get('/images', {
            params: { page }, headers: {
                authorization: `Bearer ${token}`
            }
        })
        return response.data
    } catch (error) {
        throw Error(error)
    }
}

export const getImageDetails = async (id: string) => {
    const token = await getToken()
    try {
        const response = await AxiosClient.get('/images', {
            params: { id }, headers: {
                authorization: `Bearer ${token}`
            }
        })
        return response
    } catch (error) {
        throw Error(error)
    }
}
