import React, { ReactElement, FC, useState, useEffect } from "react";
import ImageDisplayer from "../ImageDisplayer";
import { fetchImages } from "../../utils/ImageFetcher";

interface imagePage {
    hasMore: boolean,
    page: number,
    pageCount: number,
    pictures: {
        id: string,
        cropped_picture: string,
    }[],
}

const Main: FC = (): ReactElement => {
    const [page, setPage] = useState(1)
    const [currentPage, setCurrentPage] = useState<imagePage | undefined>()
    const loadedPages: imagePage[] = []

    useEffect(() => {
        fetchImages(page)
            .then(response => {
                console.log(response)
                setCurrentPage(response)
                loadedPages.push(response)
            })
    }, [page])


    return (
        <main className='py-10 px-5'>
            <h1 className='text-xl mb-8'>
                <strong>
                    AgileEngine Interview
                    </strong>
            </h1>

            <div className='flex flex-row justify-between'>
                <button className='bg-blue-400 rounded' onClick={() => currentPage?.page > 1 && setPage(page - 1)}>
                    Previous
                </button>
                <p>
                    Current page: {currentPage?.page}
                </p>
                <button className='bg-blue-400 rounded' onClick={() => currentPage?.hasMore && setPage(page + 1)}>
                    Next
                </button>
            </div>
            <ImageDisplayer page={page} />
        </main>
    )
}


export default Main
